import React,{useEffect, Fragment} from 'react';
import{ connect }  from "react-redux";
import { getPost, createPost } from '../../actions/post';
import Spinner from "../layout/Spinner";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import PostList from './PostList';


function Posts({posts, post, getPost, createPost}) {

    const [formdata, setformdata] = useState({
        text:"",

    });

    useEffect(() => {
        getPost();
    }, []);

    console.log("Posts.js", posts, post)
    const onChange =(e)=>{ setformdata({...formdata, [e.target.name]:e.target.value})};
    const onSubmit = (e)=>{
        e.preventDefault();
        createPost(formdata, history)
    }
    return (
        <Fragment>
            <h1 class="large text-primary">
                Posts
            </h1>
            <p class="lead"><i class="fas fa-user"></i> Welcome to the community!</p>

            <div class="post-form">
                <div class="bg-primary p">
                    <h3>Say Something...</h3>
                </div>
                <form class="form my-1" onSubmit={e=> onSubmit(e)}>
                    <textarea
                        name="text"
                        cols="30"
                        rows="5"
                        value={text}
                        placeholder="Create a post"
                        required
                        onChange={(e)=>onChange(e)}
                    ></textarea>
                    <input type="submit" class="btn btn-dark my-1" value="Submit" />
                </form>
            </div>

            <PostList post={post}></PostList>
        </Fragment>
    )
}

Posts.propTypes = {
    post: PropTypes.object.isRequired,
    posts : PropTypes.object.isRequired,
    getPost: PropTypes.func.isRequired,
    createPost: PropTypes.func.isRequired,

}
const mapStateToProps=state=>({
    post: state.post,
    posts : state.posts,
})
  
export default connect(mapStateToProps, { getPost, createPost })(Posts);
