import {setAlert} from './alert';
import axios from 'axios';
import{ PROFILE_ERROR, GET_PROFILE } from './types';


// current user profile
export const getCurrentUserProfile = ()=> async dispatch =>{

    try{
        debugger;
        const res = await axios.get('http://127.0.0.1:5000/api/profile/profile/')

        dispatch({
            type: GET_PROFILE,
            payload: res.data
        })

    }
    catch(err){ 
        debugger;
        dispatch({
            type: PROFILE_ERROR,
            payload: {status:err.response.status, msg:err.response.statusText}
        });
    }

}

// create update profile
export const createProfile = (fromData, history, edit) => async dispatch =>{
    try{    

        const config = {
            headers : {
                "content-type": "application/json",
            }
        }

        const res = await axios.post('http://127.0.0.1:5000/api/profile/createupdateprofile/', config, fromData)
        
        dispatch({
            type: GET_PROFILE,
            payload: res.data
        });
        dispatch(setAlert(edit? "Profile Updated": "Profile Created"));
        
        if(!edit){
            history.push('/dashboard');
        }


    }   
    catch(err){

        const errors = err.response.data.errors
        if(errors){
            errors.forEach(error => {
                dispatch(setAlert(error,"danger"))
            });
        }

        dispatch({
            type: PROFILE_ERROR,
            payload: {status:err.response.status, msg:err.response.statusText}
        });

    }
}
