import React,{useEffect, Fragment} from 'react';
import{ connect }  from "react-redux";
import { useState } from 'react'
import Spinner from "../layout/Spinner";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import {getPostById, createComment} from '../../actions/post';
import Posts from '../post/Posts';


function Comments({match,post,getPostById}) {

    const [formdata, setformdata] = useState({
        text:"",

    });
    const {
        text,
    } = formdata;

    useEffect(() => {
        getPostById(match.params.id);
    }, [getPostById, match.params.id]);
    debugger;
    console.log("comment.js", post.post)
    const onChange =(e)=>{ setformdata({...formdata, [e.target.name]:e.target.value})};
    const onSubmit = (e)=>{
        e.preventDefault();
        createComment(formdata,post._id, history)
        setformdata({...formdata, text:""})
    }


    return post.loading ? <Spinner></Spinner>:
        (
        <Fragment>
            <Link to="/posts" class="btn">Back To Posts</Link>
            <div class="post bg-white p-1 my-1">
                <div>
                <a href="profile.html">
                    <img
                    class="round-img"
                    src={post.post.gravtar}
                    alt=""
                    />
                    <h4>{post.post.name}</h4>
                </a>
                </div>
                <div>
                <p class="my-1">
                    {post.post.text}
                </p>
                </div>
            </div>

            <div class="post-form" onSubmit={e=> onSubmit(e)}> 
                <div class="bg-primary p">
                <h3>Leave A Comment</h3>
                </div>
                <form class="form my-1">
                <textarea
                    name="text"
                    cols="30"
                    rows="5"
                    value={text}
                    placeholder="Comment on this post"
                    onChange = {(e)=>onChange(e)}
                    required
                ></textarea>
                <input type="submit" class="btn btn-dark my-1" value="Submit" />
                </form>
            </div>
        </Fragment>
    )
}

Comments.propTypes = {
    post: PropTypes.object.isRequired,
    getPostById: PropTypes.func.isRequired,
    createComment: PropTypes.func.isRequired,
}

const mapStateToProps=state=>({
    post: state.post
})

export default connect(mapStateToProps, { getPostById, createComment })(Comments);
