import React,{useEffect, Fragment} from 'react';
import{ connect }  from "react-redux";
import { getAllProfile } from '../../actions/profile';
import Spinner from "../layout/Spinner";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

function Profiles({getAllProfile, auth, profile}) {

    useEffect(() => {
        getAllProfile();
    }, []);

    console.log("Profiles", profile)
    const profiles = profile.profiles.map(prof =>
        (
            <div class="profile bg-light">
            <img
                class="round-img"
                src={prof.user.avatar}
                alt=""
            />
            <div>
                <h2>{prof.user.name}</h2>
                <p>{prof.status} at {prof.company}</p>
                <p>{prof.location}</p>
                <Link class="btn btn-primary" to={`/user/${prof.user._id}`}>View Profile</Link>
            </div>

            <ul>
            { prof.skills.slice(0,4).map(skill=>(
                <li class="text-primary">
                <i class="fas fa-check"></i> {skill}
                </li>    
            ))}
            </ul>
            </div>
        )
        )
    return profile.loading ? <Spinner></Spinner>:
        <Fragment>
            <section class="container">
            <h1 class="large text-primary">Developers</h1>
            <p class="lead">
                <i class="fab fa-connectdevelop"></i> Browse and connect with developers
            </p>
        <div class="profiles">
            {profile.profiles.length>0?(
                profiles
            ):
            <h4>No Profile Found..........</h4>
            }   
        </div>
        </section>

    </Fragment>
}

Profiles.propTypes = {
    auth: PropTypes.object.isRequired,
    profile : PropTypes.object.isRequired,
    getCurrentUserProfile: PropTypes.func.isRequired,

}
const mapStateToProps=state=>({
    auth : state.auth,
    profile : state.profile,
  })
  
export default connect(mapStateToProps, { getAllProfile })(Profiles);
