import React,{useEffect, Fragment} from 'react';
import{ connect }  from "react-redux";
import Spinner from "../layout/Spinner";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import {getGithubRepo} from '../../actions/profile';

function GitHubDetail({profile,getGithubRepo,repos}) {

    useEffect(() => {
        getGithubRepo(profile.profile.handle);
    }, [getGithubRepo]);

    console.log("Github............",profile, repos)
    return (
        <Fragment>
            <div class="profile-github">
          <h2 class="text-primary my-1">
            <i class="fab fa-github"></i> Github Repos
          </h2>
          {repos !== null && repos ? repos.map(repo=>(
            <div class="repo bg-white p-1 my-1">
            <div>
              <h4><a href={repo.html_url} target="_blank"
                  rel="noopener noreferrer">{repo.name}</a></h4>
              <p>
                {repo.description}
              </p>
            </div>
            <div>
              <ul>
                    <li class="badge badge-primary">Stars: {repo.stargazers_count}</li>
                    <li class="badge badge-dark">Watchers: {repo.watchers_count}</li>
                    <li class="badge badge-light">Forks: {repo.forks_count}</li>
              </ul>
            </div>
            </div>
          )): <p>Not Found</p>}
        </div>
        </Fragment>
    )
}

GitHubDetail.propTypes = {
    profile: PropTypes.object.isRequired,
    repos: PropTypes.object.isRequired,
    getGithubRepo: PropTypes.func.isRequired,

}

const mapStateToProps=state=>({
    profile : state.profile,
    repos: state.repos
  })
  
export default connect(mapStateToProps, { getGithubRepo })(GitHubDetail);
