import React,{useEffect, Fragment} from 'react';
import{ connect }  from "react-redux";
import { getPost, deletePost } from '../../actions/post';
import Spinner from "../layout/Spinner";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

function PostList({post, deletePost, likePost}) {
    console.log("PostList", post)


    const deletePostId = (id)=>{
        deletePost(id)
    }

    const LikePostId = (id) =>{
        likePost(id)
    }

    return (
        <Fragment>
            {post.posts && post.posts !== null ? 
            post.posts.map(post=>(
                <div className="posts">
                    <div className="post bg-white p-1 my-1">
                    <div>
                        <a href="">
                        <img
                            className="round-img"
                            src={post.avatar}
                            alt=""
                        />
                        <h4>{post.name}</h4>
                        </a>
                    </div>
                    <div key={post._id}>
                        <p className="my-1">
                        {post.text}
                        </p>
                        <p className="post-date">
                            Posted on 
                        </p>
                        <button type="button" className="btn btn-light"
                        onClick= {()=>{LikePostId(post._id)}}
                        >
                        <i className="fa fa-thumbs-up"></i>
                            <span>{post.likes.length}</span>
                        </button>
                        <button type="button" className="btn btn-light">
                        <i class="fa fa-thumbs-down"></i>
                        </button>
                        <a href="post.html" className="btn btn-primary">
                            Discussion <span className='comment-count'>{post.comments.length}</span>
                        </a>
                        <button      
                        type="button"
                        onClick = {()=>{deletePostId(post._id)}}
                        className="btn btn-danger">
                        <i className="fa fa-times"></i>
                    </button>
                    </div>
                </div>
            </div>

            ))
            
            :<strong>No Posts Found</strong>}
            
        </Fragment>
    )
}

PostList.propTypes = {
    post: PropTypes.object.isRequired,
    deletePost : PropTypes.func.isRequired,
    likePost: PropTypes.func.isRequired,
}

export default PostList
