import React,{useEffect, Fragment} from 'react';
import{ connect }  from "react-redux";
import { useState } from 'react';
import Moment from 'react-moment';
import Spinner from "../layout/Spinner";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import {getPostById, createComment, removeComment} from '../../actions/post';


function CommentList({comments, auth, match}) {

    console.log("Comments", comments)

    removeCommentId = (comment_id)=>{
        removeComment(match.params.id, comment_id)
    }

    return (
        <Fragment>
            {comments.map(comment=>{
               return(<div className="post bg-white p-1 my-1" key={comment._id}>
                <div>
                    <a href="profile.html">
                    <img
                        className="round-img"
                        src={comment.avatar}
                        alt=""
                    />
                    <h4>{comment.name}</h4>
                    </a>
                </div>
                <div>
                    <p className="my-1">
                    {comment.text}
                    </p>
                    <p className="post-date">
                    Posted on <Moment format="YYYY/MM/DD">
                        {comment.date}
                    </Moment>
                    </p>
                    {auth.user._id === comment.user ?
                        <button type="button" class="btn btn-danger" onClick={()=>{removeCommentId(comment._id)}}>
                            <i class="fa fa-times"></i>
                        </button> : ""
                    }
                </div>
            </div>)
            })}
        </Fragment>
    )
}

CommentList.propTypes = {
    comments : PropTypes.object.isRequired,
    auth : PropTypes.object.isRequired,
}

export default CommentList
