import React, {Fragment} from 'react'
import {Link, withRouter} from 'react-router-dom';
import PropTypes from 'prop-types'
import{ connect }  from "react-redux";
import { useState } from 'react'
import { addExperience } from "../../actions/profile";

function Experience({addExperience, history}) {

    const [formdata, setformdata] = useState({
        title: "",
        company: "",
        location: "",
        from: "",
        to: "",
        current: false,
        description: ""
    });

    const [currentYear, toggleCurrentYear] = useState(true);

    const {
        title,
        company,
        location,
        from,
        to,
        current,
        description
    } = formdata;

    const onChange =(e)=>{ setformdata({...formdata, [e.target.name]:e.target.value})};
    const onSubmit = (e)=>{
        debugger;
        e.preventDefault();
        addExperience(formdata, history)
    }

    return (
        <Fragment>
        <h1 className="large text-primary">
            Add An Experience
        </h1>
        <p className="lead">
            <i className="fas fa-graduation-cap"></i> Add any developer/programming
        positions that you have had in the past
        </p>
        <small>* = required field</small>
        <form className="form" onSubmit={e=> onSubmit(e)}>
            <div className="form-group">
            <input
                type="text"
                placeholder="* Job Title"
                name="title"
                value = {title}
                onChange={(e)=>onChange(e)}
                required
            />
            </div>
            <div className="form-group">
            <input
                type="text"
                placeholder="* Company"
                name="company"
                value = {company}
                onChange={(e)=>onChange(e)}
                required
            />
            </div>
            <div className="form-group">
            <input type="text" placeholder="Location" name="location" value={location} onChange={(e)=>onChange(e)} />
            </div>
            <div className="form-group">
            <h4>From Date</h4>
            <input type="date" name="from" value={from} onChange={(e)=>onChange(e)} />
            </div>
            <div className="form-group">
            <p>
                <input type="checkbox" name="current" value={current} onClick={()=>{toggleCurrentYear(!currentYear)}} 
                onChange={(e)=>onChange(e)}/> Current Job
            </p>
            </div>
            {currentYear && <Fragment>
                <div className="form-group">
                    <h4>To Date</h4>
                    <input type="date" name="to" value={to} onChange={(e)=>onChange(e)}/>
                </div>
            </Fragment>}
            <div className="form-group">
            <textarea
                name="description"
                cols="30"
                rows="5"
                placeholder="Program Description"
                value= {description}
                onChange={(e)=>onChange(e)}
            ></textarea>
            </div>
            <input type="submit" className="btn btn-primary my-1" />
            <Link className="btn btn-light my-1" to="/dashboard">Go Back</Link>
        </form>
        </Fragment>
    )
}

Experience.propTypes = {
    addExperience: PropTypes.func.isRequired,

}

export default connect(null, { addExperience  })(withRouter(Experience));
