import React, {Fragment} from 'react'
import {Link, withRouter} from 'react-router-dom';
import PropTypes from 'prop-types'
import{ connect }  from "react-redux";
import { useState } from 'react'
import { addEducation } from "../../actions/profile";

function Education({addEducation, history}) {

    const [formdata, setformdata] = useState({
        school: "",
        degree: "",
        fieldofstudy: "",
        from: "",
        to: "",
        current: false,
        description: ""
    });

    const [currentYear, toggleCurrentYear] = useState(false);

    const {
        school,
        degree,
        fieldofstudy,
        from,
        to,
        current,
        description
    } = formdata;

    const onChange =(e)=>{ setformdata({...formdata, [e.target.name]:e.target.value})};
    const onSubmit = (e)=>{
        e.preventDefault();
        addEducation(formdata, history)
    }

    return (
        <Fragment>
        <h1 className="large text-primary">
            Add Your Education
        </h1>
        <p className="lead">
            <i className="fas fa-graduation-cap"></i> Add any school, bootcamp, etc that
            you have attended
        </p>
        <small>* = required field</small>
        <form className="form" onSubmit={e=> onSubmit(e)}>
            <div className="form-group">
            <input
                type="text"
                placeholder="* School or Bootcamp"
                name="school"
                value = {school}
                onChange={(e)=>onChange(e)}
                required
            />
            </div>
            <div className="form-group">
            <input
                type="text"
                placeholder="* Degree or Certificate"
                name="degree"
                value = {degree}
                onChange={(e)=>onChange(e)}
                required
            />
            </div>
            <div className="form-group">
            <input type="text" placeholder="Field Of Study" name="fieldofstudy" value={fieldofstudy} onChange={(e)=>onChange(e)} />
            </div>
            <div className="form-group">
            <h4>From Date</h4>
            <input type="date" name="from" value={from} onChange={(e)=>onChange(e)} />
            </div>
            <div className="form-group">
            <p>
                <input type="checkbox" name="current" value={current} onClick={()=>{toggleCurrentYear(!currentYear)}} 
                onChange={(e)=>onChange(e)}/> Current School or Bootcamp
            </p>
            </div>
            {displaySocialInputs && <Fragment>
                <div className="form-group">
                    <h4>To Date</h4>
                    <input type="date" name="to" value={to} onChange={(e)=>onChange(e)}/>
                </div>
            </Fragment>}
            <div className="form-group">
            <textarea
                name="description"
                cols="30"
                rows="5"
                placeholder="Program Description"
                value= {description}
                onChange={(e)=>onChange(e)}
            ></textarea>
            </div>
            <input type="submit" className="btn btn-primary my-1" />
            <Link className="btn btn-light my-1" to="/dashboard">Go Back</Link>
        </form>
        </Fragment>
    )
}

Education.propTypes = {
    addEducation: PropTypes.func.isRequired,

}

export default connect(null, { addEducation  })(withRouter(Education));
