import { GET_PROFILE, PROFILE_ERROR, ADD_EDUCATION_ERROR, CLEAR_PROFILE, GET_ALL_PROFILE,
        ALL_PROFILE_ERROR} from "../actions/types"


const initialState = {
    profile: null,
    profiles:[],
    repos: [],
    loading: true,
    error: {}

}

export default function(state=initialState, action){

    const {type, payload} = action
    switch(type){
        case GET_PROFILE:
            return {
                ...state,
                profile:payload,
                loading:false
            }
        case GET_ALL_PROFILE:
            return {
                ...state,
                profiles: payload,
                loading:false
            }
        case ALL_PROFILE_ERROR:
        case PROFILE_ERROR:
        case ADD_EDUCATION_ERROR:
            return {
                ...state,
                error: payload,
                loading: false
            }
        
        case CLEAR_PROFILE:
            return {
                ...state,
                profile:null,
                repos: [],
                loading: false
            }
        default:
            return state;
    }
}
