import React,{useEffect, Fragment} from 'react';
import{ connect }  from "react-redux";
import { getProfileByUserId } from '../../actions/profile';
import Spinner from "../layout/Spinner";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';


function ProfileDetail({match, profile, getProfileByUserId}) {

    useEffect(() => {
        getProfileByUserId(match.params.id);
    }, [getProfileByUserId, match.params.id]);

    console.log("Profile", profile)
    return profile.loading ? <Spinner></Spinner>:
        (
        <Fragment>
            <a href="profiles.html" class="btn btn-light">Back To Profiles</a>
            <div class="profile-grid my-1">
                <div class="profile-top bg-primary p-2">
                    <img
                        class="round-img my-1"
                        src="https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200"
                        alt=""
                    />
                    <h1 class="large">John Doe</h1>
                    <p class="lead">Developer at Microsoft</p>
                    <p>Seattle, WA</p>
                    <div class="icons my-1">
                        <a href="#" target="_blank" rel="noopener noreferrer">
                        <i class="fas fa-globe fa-2x"></i>
                        </a>
                        <a href="#" target="_blank" rel="noopener noreferrer">
                        <i class="fab fa-twitter fa-2x"></i>
                        </a>
                        <a href="#" target="_blank" rel="noopener noreferrer">
                        <i class="fab fa-facebook fa-2x"></i>
                        </a>
                        <a href="#" target="_blank" rel="noopener noreferrer">
                        <i class="fab fa-linkedin fa-2x"></i>
                        </a>
                        <a href="#" target="_blank" rel="noopener noreferrer">
                        <i class="fab fa-youtube fa-2x"></i>
                        </a>
                        <a href="#" target="_blank" rel="noopener noreferrer">
                        <i class="fab fa-instagram fa-2x"></i>
                        </a>
                    </div>
                </div>
            </div>
        </Fragment>
        )
}

ProfileDetail.propTypes = {
    auth: PropTypes.object.isRequired,
    profile : PropTypes.object.isRequired,
    getProfileByUserId : PropTypes.func.isRequired,

}
const mapStateToProps=state=>({
    auth : state.auth,
    profile : state.profile,
  })
  
export default connect(mapStateToProps, { getProfileByUserId })(ProfileDetail);
